from rest_framework.views import APIView
from django.http import JsonResponse
import requests
import re


class TestView(APIView):
    def dispatch(self, request, *args, **kwargs):
        """
        请求到来之后，都要执行dispatch方法，dispatch方法根据请求方式不同触发 get/post/put等方法

        注意：APIView中的dispatch方法有好多好多的功能
        """
        return super().dispatch(request, *args, **kwargs)

    def get(self, request, *args, **kwargs):
        keyword = request.GET.get('kw', '币乎')
        page = int(request.GET.get('pageno', '1'))
        pagesize = int(request.GET.get('count', '20'))

        # keyword = '圣杯'
        # page = 1
        # pagesize = 20
        keyword = keyword + ' site:bihu.com'

        url = 'http://www.google.com.hk/search'
        payload = {'ie': 'utf8', 'oe': 'utf8', 'safe': 'strict', 'q': keyword,
                   'num': pagesize, 'start': page * pagesize - pagesize,
                   'hl': 'zh-CN'}
        hdr = {'User-agent': 'Mozilla/11.0'}
        r = requests.get(url, params=payload, headers=hdr)
        r.encoding = 'utf-8'

